# Environment.pyclass Environment: def __init__(self, initial_reg=100, collapse_threshold=0, alert_threshold=20, irrevocability_multiplier=0.8): self.reg = initial_reg # Reserva de Energ√≠a Global self.initial_reg = initial_reg self.collapse_threshold = collapse_threshold self.alert_threshold = alert_threshold self.irrevocability_multiplier = irrevocability_multiplier self.in_irrevocable_state = False self.turn = 0 def update_reg(self, damages, contribs, noise=0): self.reg = self.reg - sum(damages) + sum(contribs) + noise self.turn += 1 if self.reg < self.alert_threshold: self.in_irrevocable_state = True if self.in_irrevocable_state: self.reg *= self.irrevocability_multiplier # Aplicar castigo permanente return self.reg def is_collapsed(self): return self.reg <= self.collapse_threshold def get_global_hint(self): # Simula incertidumbre: Devuelve hint parcial o noisy return self.reg * (0.8 + 0.4 * (self.turn % 2)) # Ejemplo simple de ruido 
